from PiStorms import PiStorms
import time
import random

random_speed = None

psm = PiStorms()


# Connect two motors to Bank A.
#
# This section rotates two motors in different directions for 3 seconds.
psm.BAM1.setSpeed(50)
psm.BAM2.setSpeed((-50))
time.sleep(3)
psm.BAM1.brakeSync()
time.sleep(1)

# This section rotates the first motor 180 degrees forward and backward.
psm.BAM1.runDegs(180, 50, True, False)
time.sleep(2)
psm.BAM1.runDegs((-180), 50, True, False)
time.sleep(2)

# This section rotates the motors at 10 random speeds.
for count in range(10):
  random_speed = random.randint(-127, 127)
  psm.BAM1.setSpeedSync(random_speed)
  time.sleep(0.5)
psm.BAM1.brakeSync()
