from PiStorms import PiStorms

item = None
i = None

psm = PiStorms()


# On PiStorms screen, draw circles in a diagonal line .
psm.screen.fillCircle(40, 40, 25, (255, 204, 0), display = True)
item = 0
for i in range(1, 11):
  item = item + i * 5
  psm.screen.fillCircle(item, item, 5, (255, 0, 0), display = True)
while not (bool(psm.isKeyPressed())):
  pass
